*{max-width:100%;}
@media screen {
body{margin:0;padding:0;background:#fff;font-family:Verdana,"Lucida Grande",Helvetica,sans-cerif;}p,li,blockquote,legend,dl{line-height:1.6em;}blockquote{margin-left:20px;margin-right:20px;}#content li li{font-size:0.9em;}#content li li li li{font-size:1em;}#sidebar-1,#sidebar-2{font-size:x-small;}#sidebar-1 li{font-size:1em;}#sidebar-1{text-align:right;}#sidebar-1 ul{list-style:none;margin-left:0px;padding-left:0px;}#sidebar-1 li>ul,#sidebar-1 ul.activepath{display:none;border-right:2px dotted #942C1E;padding-right:1em;}#sidebar-1 li:hover>ul,#sidebar-1 ul.activepath{display:inherit;}#sidebar-1 li.active>a,legend,.bold{font-weight:bold;}#head{text-align:center;width:100%;}#container{margin-left:155px;margin-right:155px;}#sidebar-1{overflow:auto;position:absolute;border-right:1px solid transparent;height:100%;top:0px;padding-top:10px;float:left;width:150px;margin-right:5px;}body>#sidebar-1{position:fixed;}#content{font-size:0.9em;padding:30px 35px;text-align:justify;}#content td{text-align:left;}#sidebar-2{float:right;width:150px;\width:150px;w\idth:150px;padding-left:5px;padding-top:30px;}#foot{clear:both;margin-top:5px;text-align:center;}#accessibility{position:absolute;top:-10000px;}a{color:black;text-decoration:none;border-bottom:2px dotted #942C1E;}#sidebar-2 a,#sidebar-1 a,#foot a,a:hover,#peg_parent{border-bottom:2px solid transparent;color:#942C1E;}#sidebar-2 a:hover,#sidebar-1 a:hover,#foot a:hover{color:black;border-bottom:2px dotted #942C1E;}a img{border:0px;}h1,h2,h3,h4{font-family:Georgia,Verdana,Serif;}h1,h2,h3,h4,h5{font-weight:normal;}h1{text-decoration:none;font-size:3em;text-shadow:#fff 0 0 .1em,0 0 .15em;}h2{font-size:2em;font-style:italic;}h3{font-size:1.5em;line-height:1.6em;}h4{font-size:1.33em;counter-reset:h5;}h5{font-size:1.1em;font-variant:small-caps;counter-reset:h6;}h5:before{counter-increment:h5;content:counter(h5,lower-alpha) " - "}h6{font-size:0.9em;/*font-variant:small-caps;*/}h3 a{font-size:1.1em;}h1 a,h2 a,h3 a,.tabprojet a{border:0px;transition:color 0.5s;-webkit-transition:color 0.5s;}.caps{font-size:0.9em;letter-spacing:0.1em;}#content hr{color:#ccc;width:66%;margin:2em 0;}img.divider{text-align:center;margin:2em 0;}img.photo{border:2px solid white;box-shadow:0 0 4px #6F7385;}.old,del{opacity:0.5;}.old:hover{opacity:1;transition:0.5s;-webkit-transition:0.5s;}.comments_error,.filterpopup{background-color:#ffffa8;border:1px solid #decf95;}.comments_error{padding:0.3em;box-shadow:inset 0 0 8px #decf95;}*:not(input):target{/*box-shadow:0 0 8px 1px LightSteelBlue;border-radius:2px;border:1px solid LightSteelBlue;*/outline-offset:2px;outline:2px dotted #942C1E;}textarea{width:100%;}.txpComment{background:#F3F2EC;border:1px solid lightgrey;box-shadow:inset 0 0 4px white;padding:1em;}.txpComment input,.txpComment textarea{box-shadow:0 0 4px white;}ul.comments{border-left:2px dotted #b2b4bf;padding-left:1em;margin-left:1em;/*opacity:0.5;*/}ul.comments>li{list-style:none;}/*ul.comments:hover,.comments *:hover ~ ul.comments{color:#942C1E;border-left:2px dotted black;opacity:1;}.comments *:hover ~ ul.comments ul.comments{color:black;}*/table.tabprojet{break-inside:avoid-column;background:url('images/tab-middle-bkgnd.png') repeat-x #e0dfe3;border:1px solid #b2b4bf;border-top:0px;width:100%;}.tabprojet tbody tr{background-color:#fff;/*box-shadow:inset 0 0 12px #e0dfe3;*/}.tabprojet tbody tr td:first-child:hover>a{color:#942C1E;}.tabprojet tr.personal{background:#FFFFDB;box-shadow:inset 0 0 12px #decf95;}.tabprojet tbody td{border:1px solid #b2b4bf;}.tabprojet tfoot td{font-weight: bold;background:linear-gradient(#b2b4bf,#e0dfe3);background:-webkit-linear-gradient(90deg,#e0dfe3,#b2b4bf);}.avatar{border:1px solid #b2b4bf;float:left;height:2ex;width:2ex;margin-right:1em;}.popup{display:none;position:absolute;}*:hover>.popup{display:block;}.pdesc{font-size:small;width:400px;margin-left:2em;margin-top:3ex;text-shadow: 1px 1px 2px #6F7385;font-style:normal;color:white;background:rgba(148,151,166,0.95);border:1px solid #6F7385;padding:0.5em 1em;box-shadow: 2px 2px 8px #6F7385;overflow:auto;resize:both;}.filterpopup{font-size:x-small;font-style:normal;text-align:left;padding:0.5em;font-weight:normal;position:absolute;display:none;box-shadow: 2px 2px 8px #decf95;border-radius:0.5em;}.ptitre:hover,.pvalid:hover,.active,.emphcolor{color:#942C1E;}*:hover>.filterpopup{display:block;}.ptitre,.ptitre a{font-style:italic;}.sortarrow img{border:0px;width:16px;}.filterpopup ul{list-style-type:none;padding:0px;margin:0px;}.filterpopup a{cursor:pointer;}.small{font-family:Verdana,"Lucida Grande",Helvetica,sans-cerif;font-size:x-small;}.sc{font-variant:small-caps;}*>.ietabfix{width:100%;}.emoticon {vertical-align:middle;}.article-image{float:left;margin-right:1em;}#content img{height:auto;}#content{/*overflow:auto;*/-webkit-column-width:560px;-webkit-column-gap:45px;/**/-moz-column-width:560px;-moz-column-gap:45px;column-width:560px;column-gap:45px;}.stamp{position:absolute;right:0px;color:#942C1E;font-size:2em;margin:2em 1em;padding:0.5em;border:2px dotted #942C1E;transform:rotate(30deg);-webkit-transform:rotate(30deg);-ms-transform:rotate(30deg);}#live_search input.ls_inactive{color:#ccc;}#live_search .ls_wait_image{display: inline-block;width:16px;height:16px;background:url(images/24.gif) no-repeat center;margin-left:2px;}#live_search .ls_results{overflow:hidden;width:150px;}a *.bg,a:hover *.fg,.hidden{display:none;}a:hover *.bg{display:inherit;}footer,ul.ulflat{padding:0px;margin:0px;text-align:center;}ul.ulflat li{display:inline;margin:0 0.3em;}.floatleft,.ulflat li[data-rel="prev"]{float:left;}.floatright,.ulflat li[data-rel="next"]{float:right;}
.alert {
  animation-duration: 1s;
  animation-name: slidein;
  animation-iteration-count: infinite;
  animation-direction: alternate;
  animation-timing-function: ease-out, ease-in;}
}
@media print {
#sidebar-2, #sidebar-1, #head, #foot, #accessibility, #stamp, .sortarrow, .filterpopup, .pdesc, #txpCommentInputForm, .ratingblock, .comments_invite, .comments, footer, h3 a img
{display: none;}a{color:black; text-decoration:none;}table{width:100%;}table, th, td{border: 1px solid black;}#content img{height:auto;}
}
@media only screen and (max-device-width: 640px) {
body{/*font-family:Helvetica,Verdana;*/padding:0.5em;}body[orient="landscape"]{font-size:12px;}#sidebar-2,#sidebar-1 img,#sidebar-1 #my_menu_name,#sidebar-1 canvas,#accessibility{display:none;}body>#sidebar-1{float:none;position:static;width:100%;font-size:small;margin:0;padding:0;}#sidebar-1>p,#sidebar-1>ul{float:left;margin-right:2em;}#container{min-width:0;margin:0}#content{font-size:1em;margin:0;padding:0;}.small{font-size:small;}
}
@-moz-keyframes slidein {from {opacity:1;} 90% {opacity:0.5;} to {opacity:0.75;}
}